import React from "react";
import styled from "styled-components";
import { LazyLoadImage } from "react-lazy-load-image-component";
import { Avatar } from "@mui/material";
import { DownloadRounded } from "@mui/icons-material";
import { FileSaver } from "file-saver";

const Card = styled.div`
  position: relative;
  display: flex;
  border-radius: 40px;
  box-shadow: 1px 2px 40px 8px ${({ theme }) => theme.white + 60};
  cursor: pointer;
  transition: all 0.3s ease;
  &:hover {
    box-shadow: 1px 2px 40px 8px ${({ theme }) => theme.white + 80};
    scale: 1.05;
  }
  &:nth-child(7n + 1) {
    grid-column: auto/span 2;
    grid-row: auto/span 2;
  }
`;
const HoverOverLay = styled.div`
  opacity: 0;
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  display: flex;
  flex-direction: column;
  align-items: start;
  gap: 10px;
  backdrop-filter: blur(2px);
  background: rgba(0, 0, 0, 0.5);
  color: ${({ theme }) => theme.white};
  transition: opacity 0.3s ease;
  border-radius: 6px;
  justify-content: flex-end;
  padding: 12px;

  ${Card}:hover & {
    opacity: 1;
  }
`;
const Prompt = styled.div`
  font-weight: 400px;
  font-size: 15px;
  color: ${({ theme }) => theme.white};
`;
const Author = styled.div`
  font-weight: 600px;
  font-size: 14px;
  display: flex;
  gap: 8px;
  align-items: center;
  color: ${({ theme }) => theme.white};
`;

const ImageCard = ({ item }) => {
  return (
    <Card>
      <LazyLoadImage
        width="100%"
        src="https://img.freepik.com/premium-photo/close-up-leaf-with-drops-water-it-generative-ai_797840-2114.jpg"
      />
      <HoverOverLay>
        <Prompt>{item?.prompt}</Prompt>
        <div
          style={{
            width: "100%",
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
          }}
        >
          <Author>
            {" "}
            {item?.author}
            <Avatar sx={{ width: "32px", height: "32px" }}>
              {item?.author[0]}
            </Avatar>
            Rishav
          </Author>
          <DownloadRounded onClick={() => FileSaver.saveAs(item?.prompt)} />
        </div>
      </HoverOverLay>
    </Card>
  );
};

export default ImageCard;
