import React from "react";
import styled from "styled-components";
import { LazyLoadImage } from "react-lazy-load-image-component";

const Card = styled.div`
  box-shadow: 1px 2px 40px 8px ${({ theme }) => theme.white + 60};
  cursor: pointer;
  transition: all 0.3 ease;
  &hover {
    box-shadow: 1px 2px 40px 8px ${({ theme }) => theme.white + 80};
    scale: 1.05;
  }
`;

const ImageCard = () => {
  return (
    <Card>
      <LazyLoadImage src="https://img.freepik.com/premium-photo/close-up-leaf-with-drops-water-it-generative-ai_797840-2114.jpg" />
      <HoverOverLay>
        <Prompt>Prompt</Prompt>
        <Author>Rishav</Author>
      </HoverOverLay>
    </Card>
  );
};

export default ImageCard;
